import React, { useState } from "react";

const Todo = (props) => {
  const {todo, toggleTodo} = props;
  return (
    <div className="todo" >
      <div className="item-value" id={"todo-" + todo.id}>
        <input type="checkbox" className="ckbox" checked={todo.isCompleted} onChange={() => {
          toggleTodo(todo.id)
        }} />
        <p id={"todo-" + todo.id}>{todo.value}</p>
        <input type="text" className="edit" id={"todo-" + todo.id} />
      </div>
      <div className="delet-div" id={"todo-" + todo.id}>

      </div>
    </div>
  )
}

export default Todo;
