import React, { useState } from 'react';
import './App.css';

function App() {
  let [inputTodo, setInputValue] = useState('');
  function getInputValue(e) {
    const {value} = e.target;
    setInputValue(value)
  }
  console.log(inputTodo)

  return (
    <div className="wrapper">
      <div className="heading">
        <h1>Todos</h1>
      </div>
      <div className="todo-list"></div>
      <form className="add-todo-form">
        <input type="text" className="add-todo-input" placeholder="Add todo here!" onChange={getInputValue}/>
      </form>
    </div>
  );
}


export default App;
