import React,{ useState } from "react";

const todoState = {
  todoArr: [],
}

let todoValue, setTodoValue;
export function inputHandler(e) {
  [todoValue, setTodoValue] = useState('');
  setTodoValue(e.target.value);
}

function setTodo() {
  todoState.todoArr.push({
    value:todoValue,
    isCompleted: false,
    id:Date.now()
  })

  console.log(todoState)
}
