import React, { useState } from 'react';
import './App.css';
// import {inputHandler} from './todos';

const todoState = {
  todoArr: [],
}

let todoValue, setTodoValue;

function setTodo() {
  todoState.todoArr.push({
    value: todoValue,
    isCompleted: false,
    id: Date.now()
  })

  console.log(todoState)
}

function App() {
  [todoValue, setTodoValue] = useState('');

  function inputHandler(e) {
    e.preventDefault()

    console.log(todoValue)
    setTodo()
  }

  function handleInputChange(e) {
    setTodoValue(e.target.value);
  }
  return (
    <div className="wrapper">
      <div className="heading">
        <h1>Todos</h1>
      </div>
      <div className="todo-list"></div>
      <form className="add-todo-form" onSubmit={inputHandler}>
        <input type="text" className="add-todo-input" placeholder="Add todo here!" onChange={handleInputChange}/>
      </form>
    </div>
  );
}


export default App;
