import React, { useState } from 'react';
import './App.css';
import {inputHandler} from './todos';

function App() {

  return (
    <div className="wrapper">
      <div className="heading">
        <h1>Todos</h1>
      </div>
      <div className="todo-list"></div>
      <form className="add-todo-form" onSubmit={inputHandler}>
        <input type="text" className="add-todo-input" placeholder="Add todo here!"/>
      </form>
    </div>
  );
}


export default App;
