import React, { useState } from 'react';
import './App.css';
import Todo from "./todos";

function App() {
  const [todoValue, setTodoValue] = useState('');
  const [todoArr, setTodoArr] = useState([]);

  function inputHandler(e) {
    e.preventDefault()

    const todoObj = {
      value: todoValue,
      isCompleted: false,
      id: Date.now()
    }
    // console.log(todoValue)
    const arr = [...todoArr, todoObj]
    setTodoArr(arr)
    setTodoValue("")
  }
  console.log(todoArr)

  let listTodos = todoArr.map(todo => {
    return <Todo todo={todo} />;
  })

  function handleInputChange(e) {
    setTodoValue(e.target.value);
  }

  return (
    <div className="wrapper">
      <div className="heading">
        <h1>Todos</h1>
      </div>
      <div className="todo-list">{listTodos}</div>
      <form className="add-todo-form" onSubmit={inputHandler}>
        <input value={todoValue} type="text" className="add-todo-input" placeholder="Add todo here!" onChange={handleInputChange} />
      </form>
    </div>
  );
}

export default App;
