import React, { useState } from 'react';
import './App.css';

function App() {
  const [todoValue, setTodoValue] = useState('');
  const [todoArr, setTodoArr] = useState([]);

  function inputHandler(e) {
    e.preventDefault()

    const todoObj = {
      value: todoValue,
      isCompleted: false,
      id: Date.now()
    }
    // console.log(todoValue)
    const arr = [...todoArr, todoObj]
    setTodoArr(arr)


  }
  console.log(todoArr)

  let listTodos = todoArr.map(todo => {
    return <div className="todo" key={"todo-" + todo.id}>
      <div className="item-value" id={"todo-" + todo.id}>
        <input type="checkbox" id={"todo-" + todo.id} />
        <p id={"todo-" + todo.id}>{todo.value}</p>
        <input type="text" className="edit" id={"todo-" + todo.id} />
      </div>
      <div className="delet-div" id={"todo-" + todo.id}>

      </div>
    </div>
  })

  function handleInputChange(e) {
    setTodoValue(e.target.value);
  }

  return (
    <div className="wrapper">
      <div className="heading">
        <h1>Todos</h1>
      </div>
      <div className="todo-list">{listTodos}</div>
      <form className="add-todo-form" onSubmit={inputHandler}>
        <input type="text" className="add-todo-input" placeholder="Add todo here!" onChange={handleInputChange} />
      </form>
    </div>
  );
}

